mapper.select:
SELECT DISTINCT room_id
  FROM mvc_chat6
 WHERE sender = #{userId}
    OR receiver = #{userId}

SELECT * FROM ( SELECT DISTINCT * FROM mvc_chat6 WHERE room_id = #{roomId}
AND ( content LIKE '%' || #{searchText} || '%' OR receiver LIKE '%' || #{searchText} || '%' OR sender LIKE '%' || #{searchText} || '%' )
ORDER BY message_id DESC ) WHERE ROWNUM = 1

choose.when:
		SELECT DISTINCT receiver
  		  FROM mvc_chat6
 		 WHERE room_id = #{roomId}

choose.otherwise:
		SELECT DISTINCT sender
  		  FROM mvc_chat6
 		 WHERE room_id = #{roomId}

mapper.select:
SELECT COUNT(*)
  FROM mvc_chat6
 WHERE room_Id = #{roomId}
   AND receiver = #{userId}
   AND is_read = 'N'

mapper.update:
UPDATE mvc_chat6
   SET is_read = 'Y'
 WHERE room_id = #{roomId}
   AND receiver = #{receiver}
