mapper.update:
UPDATE mvc_board6
   SET bName = #{bName},
   	   bTitle = #{bTitle},
   	   bContent = #{bContent}
 WHERE bId = #{bId}

UPDATE mvc_board6
   SET bHit = bHit + 1
 WHERE bId = #{bId}

UPDATE mvc_board6
   SET bLike = bLike + 1
 WHERE bId = #{bId}

UPDATE mvc_board6
   SET bLike = bLike - 1
 WHERE bId = #{bId}

mapper.select:
SELECT bLike
  FROM mvc_board6
 WHERE bId = #{bId}

mapper.update:
UPDATE mvc_board6
   SET bBookmark = bBookmark + 1
 WHERE bId= #{bId}

UPDATE mvc_board6
   SET bBookmark = bBookmark - 1
 WHERE bId = #{bId}

mapper.select:
SELECT bBookmark
  FROM mvc_board6
 WHERE bId = #{bId}

SELECT * FROM ( SELECT t.*, ROWNUM AS rnum FROM ( SELECT * FROM mvc_board6 WHERE bStep = 0 AND bIndent = 0
AND ${searchGubun} LIKE '%' || #{searchText} ||'%'
ORDER BY bId DESC
ORDER BY bHit DESC, bId DESC
) t ) WHERE rnum BETWEEN #{pagingStart} AND #{pagingEnd}

SELECT * FROM ( SELECT t.*, ROWNUM AS rnum FROM ( SELECT * FROM mvc_board6 WHERE bGroup = #{bGroup} AND bStep > 0 AND bIndent > 0
ORDER BY bStep ASC
ORDER BY bStep ASC
) t ) WHERE rnum BETWEEN #{commentsPagingStart} AND #{commentsPagingEnd}

SELECT * FROM ( SELECT t.*, ROWNUM AS rnum FROM ( SELECT * FROM mvc_board6 WHERE bStep = 0 AND bIndent = 0
AND ${searchGubun} LIKE '%' || #{searchText} ||'%'
ORDER BY bHit DESC, bId DESC ) t ) WHERE rnum BETWEEN #{pagingStart} AND #{pagingEnd}
